    public PizzaDirector(PizzaBuilder builder) {
        this.builder = builder;
    }

    // Predefined "recipe" for a Margherita Pizza
    public Pizza constructMargherita() {
        return builder.setSize("Medium")
                     .setCrust("Thin")
                     .addTopping("Cheese")
                     .addTopping("Basil")
                     .setSauce("Tomato")
                     .build();
    }

